package com.example.githubs.adapterimport androidx.annotation.StringResimport androidx.appcompat.app.AppCompatActivityimport androidx.fragment.app.Fragmentimport androidx.viewpager2.adapter.FragmentStateAdapterimport com.example.githubs.Rimport com.example.githubs.ui.detail.FollowerFragmentimport com.example.githubs.ui.detail.FollowingFragmentclass PagerAdapter(activity: AppCompatActivity) : FragmentStateAdapter(activity) {    companion object {        @StringRes        val TAB_TITLE = intArrayOf(R.string.tab1, R.string.tab2)    }    override fun getItemCount(): Int = 2    override fun createFragment(position: Int): Fragment {        var fragment: Fragment? = null        when (position) {            0 -> fragment = FollowerFragment()            1 -> fragment = FollowingFragment()        }        return fragment as Fragment    }}