package com.example.githubs.apiimport com.example.githubs.model.DetailUsersimport com.example.githubs.model.ListUsersimport com.example.githubs.model.ResponseGithubimport retrofit2.Callimport retrofit2.http.GETimport retrofit2.http.Headersimport retrofit2.http.Pathimport retrofit2.http.Queryinterface ApiService {    @GET("/search/users")    @Headers("Authorization: token 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")    fun searhUsers(@Query("q")query: String):Call<ResponseGithub>    @GET("users/{username}")    @Headers("Authorization: token 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")    fun getDetailUsers(@Path("username")username: String): Call<DetailUsers>    @GET("users/{username}/followers")    @Headers("Authorization: token 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")    fun getFollowers(@Path("username") username: String) : Call<List<ListUsers>>    @GET("users/{username}/following")    @Headers("Authorization: token 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")    fun getFollowing(@Path("username") username: String) : Call<List<ListUsers>>}