package com.example.githubs.modelimport com.google.gson.annotations.SerializedNamedata class ResponseGithub(    val items: List<ListUsers>)data class ListUsers(    val id: Int,    @field:SerializedName("login")    val username: String,    @field:SerializedName("avatar_url")    val avatar: String)data class DetailUsers(    val id: Int,    @field:SerializedName("login")    val username: String,    @field:SerializedName("name")    val name: String,    @field:SerializedName("followers")    val followers: Int,    @field:SerializedName("following")    val following: Int,    @field:SerializedName("company")    val company: String,    @field:SerializedName("avatar_url")    val avatar: String,    @field:SerializedName("follower_url")    val follower_url: String,    @field:SerializedName("following_url")    val following_url: String,    @field:SerializedName("public_repos")    val repo: Int,    @field:SerializedName("location")    val location: String)