package com.example.githubs.room.databaseimport androidx.lifecycle.LiveDataimport androidx.room.Daoimport androidx.room.Insertimport androidx.room.Queryimport com.example.githubs.room.entity.UsersFavorite@Daointerface UsersDao {    @Insert    suspend fun addFavoriteUsers(usersFavorite: UsersFavorite)    @Query("SELECT * FROM favorite")    fun getFavorite(): LiveData<List<UsersFavorite>>    @Query("SELECT count(*) FROM favorite WHERE favorite.id = :id")    suspend fun checkFavorite(id: Int): Int    @Query("DELETE FROM favorite WHERE favorite.id = :id")    suspend fun deleteFromFavorite(id: Int): Int    @Query("DELETE FROM favorite")    suspend fun deleteAllFavorite()}