package com.example.githubs.room.databaseimport android.content.Contextimport androidx.room.Databaseimport androidx.room.Roomimport androidx.room.RoomDatabaseimport com.example.githubs.room.entity.UsersFavorite@Database(entities = [UsersFavorite::class], version = 1)abstract class UsersDatabaseRoom : RoomDatabase() {    abstract fun usersDao(): UsersDao    companion object {        @Volatile        private var INSTANCE: UsersDatabaseRoom? = null        @JvmStatic        fun getDatabase(context: Context): UsersDatabaseRoom {            if (INSTANCE == null) {                synchronized(UsersDatabaseRoom::class.java) {                    INSTANCE = Room.databaseBuilder(                        context.applicationContext,                        UsersDatabaseRoom::class.java, "note_database"                    )                        .build()                }            }            return INSTANCE as UsersDatabaseRoom        }    }}