package com.example.githubs.ui.favoriteimport android.app.Applicationimport androidx.lifecycle.AndroidViewModelimport androidx.lifecycle.LiveDataimport androidx.lifecycle.viewModelScopeimport com.example.githubs.repository.UsersRepositoryimport com.example.githubs.room.entity.UsersFavoriteimport kotlinx.coroutines.Dispatchersimport kotlinx.coroutines.launchclass FavoriteViewModel(application: Application) : AndroidViewModel(application) {    private val mUsersRepository: UsersRepository = UsersRepository(application)    fun getAllFavorite(): LiveData<List<UsersFavorite>> {        return mUsersRepository.getAllFavorite()    }    fun deleteAllFavoite() {        viewModelScope.launch(Dispatchers.IO) {            mUsersRepository.deleteAllFavorite()        }    }}